<?phpnamespace Core;class SimpleDotEnv{    private string $path;    function __construct() {         $rootPath = __DIR__ . "/../";         if(file_exists($rootPath.".env")){             $this->path = $rootPath."/.env";             $this->parse();         }    }    static function env($key,$default){        if(function_exists('putenv')):            return getenv($key) === false ? $default : getenv($key);        else:            return $_ENV[$key] ?? $default;            endif;    }    private function parse(){        if (!is_readable($this->path)) {            throw new \RuntimeException(sprintf('%s file is not readable', $this->path));        }        $content = file_get_contents($this->path);        $lines = explode("\n",$content);        if(sizeof($lines) <= 1){            $lines = explode("\r",$content);        }        foreach ($lines as $line) {            if (strpos(trim($line), '#') === 0) {                continue;            }            $nameValue = explode('=', $line, 2);            if(sizeof($nameValue) !== 2){                continue;            }            list($name, $value) = $nameValue;            $name = trim($name);            $value = trim($value);            if (!array_key_exists($name, $_SERVER) && !array_key_exists($name, $_ENV)) {                if(function_exists('putenv')):                    putenv(sprintf('%s=%s', $name, $value));                endif;                $_ENV[$name] = $value;            }        }    }}